queryList.query:
SELECT MEM_SID
FROM AT_APPLY
WHERE 1=1
AND   MIS_ID = #{misId}
AND   MEM_SID = #{memSid}

UPDATE AT_APPLY
SET  STATUS = '00003'
WHERE 1=1
AND   MIS_ID = #{misId}
AND   MEM_SID = #{memSid}

DELETE FROM AT_APPLY
WHERE 1=1
AND   MIS_ID = #{misId}
AND   MEM_SID = #{memSid}

UPDATE AT_APPLY
SET   STATUS = '00005'
     ,RMK = #{rmk}
WHERE 1=1
AND   MIS_ID = #{misId}
AND   MEM_SID = #{memSid}
