queryList.query:
SELECT APY.MIS_ID ,APY.MEM_SID ,BSC.PHOTO_ID ,BSC.NAME ,BSC.PHONE1 ,BSC.PHONE2 ,BSC.BELONG_CTG1 ,BSC.BELONG_CTG2 ,BSC.BELONG_CTG3 ,FGCHURCHINFO.ATFUNC_B_GETNM(BSC.BELONG_CTG1) BELONG_CTG1_NM ,FGCHURCHINFO.ATFUNC_B_GETNM(BSC.BELONG_CTG2) BELONG_CTG2_NM ,FGCHURCHINFO.ATFUNC_B_GETNM(BSC.BELONG_CTG3) BELONG_CTG3_NM ,APY.STATUS ,ATFUNC_Z_GETGCODEDNM('A0003',APY.STATUS) STATUS_NM ,APY.FEE_YN ,APY.BOOKREPORT_YN FROM AT_APPLY APY LEFT OUTER JOIN AT_APPLY_M_BSC BSC ON APY.MIS_ID = BSC.MIS_ID AND APY.MEM_SID = BSC.MEM_SID WHERE 1=1 AND APY.MIS_ID = #{mis_id} AND APY.APPLY_CTG = '00001'
AND APY.STATUS = #{status}
AND BSC.BELONG_CTG1 = #{belong_ctg1}
AND BSC.BELONG_CTG2 = #{belong_ctg2}
AND BSC.BELONG_CTG3 = #{belong_ctg3}
AND CONCAT(BSC.NAME,'|', IFNULL(BSC.PHONE1,' '),'|',IFNULL(BSC.PHONE2,' ')) LIKE CONCAT('%',#{searchText},'%')
ORDER BY APY.MOD_DATE DESC
ORDER BY BSC.NAME ASC
